import { useEffect, useState } from "react";
import StockWidget from "../../../widget/stockgraph/StockWidget";
// import AutoComplete from "../../test/test";
import styles from "./Area.module.css";
import styled from "styled-components";
import axios from "axios";

const wholeTextArray = [];

// prettier-ignore
const data = `{
  "symbols":[["삼성전자", "005930|1D"]
  ],
  "chartOnly": false,
  "width": "700",
  "height": "450",
  "locale": "en",
  "colorTheme": "dark",
  "autosize": false,
  "showVolume": false,
  "showMA": false,
  "hideDateRanges": false,
  "hideMarketStatus": false,
  "hideSymbolLogo": false,
  "scalePosition": "right",
  "scaleMode": "Normal",
  "fontFamily": "-apple-system, BlinkMacSystemFont, Trebuchet MS, Roboto, Ubuntu, sans-serif",
  "fontSize": "10",
  "noTimeScale": false,
  "valuesTracking": "1",
  "changeMode": "price-and-percent",
  "chartType": "line",
  "maLineColor": "#2962FF",
  "maLineWidth": 1,
  "maLength": 9
}`;

export default function Area() {
  const [stockData, setStockData] = useState(data);

  const [gptValue, setGptValue] = useState("");
  const [gptRes, setGptRes] = useState("");

  // input에 입력한 값 includes를 사용
  const [inputValue, setInputValue] = useState("");

  // input값이 있으면 true 없으면 false
  const [isHaveInputValue, setIsHaveInputValue] = useState(false);

  // dropdwonlist를 DB와 연동해 set을 하면 됨.
  const [dropDownList, setDropDownList] = useState([]);
  const [secondDropDownList, setSecondDropDownList] = useState([]);

  // dropDownList에 있는 요소를 선택하기 위해 마우스를 올렸을 때 효과를 주기 위한 인덱스 번호 설정
  const [dropDownItemIndex, setDropDownItemIndex] = useState(-1);

  // 주식 설명 받아오기
  const [stockInfo, setStockInfo] = useState([]);

  const showDropDownList = () => {
    if (inputValue === "") {
      setIsHaveInputValue(false);
      setDropDownList([]);
    } else {
      const choosenTextList = wholeTextArray.filter((textItem) =>
        textItem.includes(inputValue)
      );
      setDropDownList(choosenTextList);
    }
  };

  useEffect(() => {
    fetch(
      `https://port-0-alltu-search-6g2llfxqu2r0.sel3.cloudtype.app/search/nas`
    )
      .then((res) => res.json())
      .then((data) => {
        setSecondDropDownList(data);
      });
  }, []);

  const changeInputValue = (event) => {
    setInputValue(event.target.value);
    console.log(event.target.value);
    setIsHaveInputValue(true);
    fetch(
      `https://port-0-alltu-search-6g2llfxqu2r0.sel3.cloudtype.app/search?company=${event.target.value}`
    )
      .then((res) => res.json())
      .then((data) => {
        // setSecondDropDownList(data);
        setDropDownList(data);
      });
  };

  const clickDropDownItem = (clickedItem) => {
    setInputValue(clickedItem);
    setIsHaveInputValue(false);
  };

  const handleDropDownKey = (event) => {
    //input에 값이 있을때만 작동
    if (isHaveInputValue) {
      if (
        event.key === "ArrowDown" &&
        dropDownList.length - 1 > dropDownItemIndex
      ) {
        setDropDownItemIndex(dropDownItemIndex + 1);
      }

      if (event.key === "ArrowUp" && dropDownItemIndex >= 0)
        setDropDownItemIndex(dropDownItemIndex - 1);
      if (event.key === "Enter" && dropDownItemIndex >= 0) {
        clickDropDownItem(dropDownList[dropDownItemIndex]);
        setDropDownItemIndex(-1);
      }
    }
  };

  useEffect(showDropDownList, [inputValue]);

  const PushData = (ticker) => {
    let JsonData = JSON.parse(stockData);
    JsonData.symbols.pop(0);
    JsonData.symbols.push([`${inputValue}`, `${ticker}|1D`]);
    let LastData = JSON.stringify(JsonData);
    setStockData(LastData);
  };

  const sendInputValue = (e) => {
    e.preventDefault();
    console.log("함수진입", e.target.value);
    console.log("함수진입", inputValue);
    for (let i of secondDropDownList) {
      console.log("for문 중...");
      if (i.company === inputValue) {
        console.log("티커", i.ticker);
        PushData(i.ticker);
      }
    }
    setInputValue("");
  };

  const sendInputValueBtn = () => {
    for (let i of secondDropDownList) {
      console.log("for문 중...");
      if (i.company === inputValue) {
        console.log("티커", i.ticker);
        PushData(i.ticker);
      }
    }
    setInputValue("");
  };

  const getGPT = async () => {
    axios
      .post(
        "https://wdcnuqnh7l3aono7mdgjpdbw3i0jmxss.lambda-url.ap-northeast-2.on.aws/",
        {
          question: gptValue,
        }
      )
      .then((response) => {
        setGptRes(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const sendChatGPT = (e) => {
    e.preventDefault();
    getGPT();
    setGptValue("");
  };

  // 주식 정보 받아오기 영역
  const getStockInfo = () => {
    axios
      .get(
        "https://vzjj45kvapsj55iz7apot27wiq0ddvnm.lambda-url.ap-northeast-2.on.aws/",
        {
          //prettier-ignore
          "ticker": "000080",
        }
      )
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  getStockInfo();
  return (
    <>
      <div className={styles.stockArea}>
        <StockWidget data={stockData} className={styles.stockgraph} />
        <div className={styles.stockEx}>
          <h5 style={{ color: "white" }}>향후 전망?</h5>
          <h5 style={{ color: "white" }}>{stockData.symbols}</h5>
        </div>
      </div>
      <div className={styles.searchArea}>
        <WholeBox>
          {/* <OutlineInputBox> */}
          <InputBox
            className={styles.inputBox}
            isHaveInputValue={isHaveInputValue}
          >
            <form onSubmit={sendInputValue}>
              <Input
                type="text"
                value={inputValue}
                onChange={changeInputValue}
                onKeyUp={handleDropDownKey}
              />
            </form>
            {/* <SearchButton
              onClick={() => {
                sendInputValueBtn();
              }}
            >
              &#43;
            </SearchButton> */}
          </InputBox>
          {/* </OutlineInputBox> */}
          {isHaveInputValue && (
            <DropDownBox>
              {dropDownList.length === 0 && (
                <DropDownItem>해당하는 단어가 없습니다</DropDownItem>
              )}
              {dropDownList.map((dropDownItem, dropDownIndex) => {
                return (
                  <DropDownItem
                    key={dropDownIndex}
                    onClick={() => clickDropDownItem(dropDownItem)}
                    onMouseOver={() => setDropDownItemIndex(dropDownIndex)}
                    className={
                      dropDownItemIndex === dropDownIndex ? "selected" : ""
                    }
                  >
                    {dropDownItem}
                  </DropDownItem>
                );
              })}
            </DropDownBox>
          )}
        </WholeBox>
        <div style={{}}>
          <span>답변 : {gptRes}</span>
          <form onSubmit={sendChatGPT}>
            <input
              type="text"
              onChange={(e) => {
                setGptValue(e.target.value);
              }}
              value={gptValue}
            ></input>
            <button type="submit">보내기</button>
          </form>
        </div>
      </div>
    </>
  );
}

const WholeBox = styled.div`
  padding: 10px;
`;

const OutlineInputBox = styled.div`
  border: 7px solid black;
  border-radius: 5px;
`;

const InputBox = styled.div`
  display: flex;
  flex-direction: row;
  padding: 16px;
  border: 7px solid black;
  border-radius: 20px;
  box-shadow: 3px 3px 3px 3px rgba(0, 0, 0, 0.3);

  &:focus-within {
    box-shadow: 0 10px 10px rgb(0, 0, 0, 0.3);
  }
`;

const Input = styled.input`
  flex: 1;
  margin: 0;
  background-color: transparent;
  border: none;
  outline: none;
  font-size: 32px;
  width: 15vw;
`;

const SearchButton = styled.div`
  cursor: pointer;
  padding: 0;
  margin: 0;
  color: white;
  font-size: 30px;
`;

const DropDownBox = styled.ul`
  display: block;
  margin: 0 auto;
  padding: 8px 0;
  background-color: white;
  border: 10px solid rgba(0, 0, 0, 1);
  border-top: none;
  border-radius: 0 0 16px 16px;
  box-shadow: 0 10px 10px rgb(0, 0, 0, 0.3);
  list-style-type: none;
  z-index: 3;
`;

const DropDownItem = styled.li`
  padding: 0 16px;

  &.selected {
    background-color: lightgray;
  }
`;
