import React, { useEffect, useState } from "react";
import styles from "../main/Main.module.css";
import TagCard from "../../component/maintag/TagCard";
import { CSSTransition } from "react-transition-group";
import "./styles.css";
import Test from "../../component/test/test";

function Main() {
  const [firstTag, setFirstTag] = useState(false);

  useEffect(() => {
    setFirstTag(true);
  }, []);

  return (
    <div className={styles.container}>
      <div className={styles.main}>
        <CSSTransition in={firstTag} classNames="mainImg" timeout={2000}>
          <div className={styles.mainImg}></div>
        </CSSTransition>
        <div className={styles.mainEx}>
          <CSSTransition in={firstTag} classNames="mainTitle" timeout={2500}>
            <h1>
              당신의 <br />
              올바른 투자 습관을 위해
            </h1>
          </CSSTransition>
          <CSSTransition in={firstTag} classNames="mainEx" timeout={3000}>
            <p>
              Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vivamus
              eget metus in elit rutrum lobortis. Sed volutpat sagittis sem, eu
              mattis quam luctus ut. Aliquam posuere vulputate leo eget finibus.
              Quisque gravida elit et purus cursus maximus. Duis efficitur, nisl
              ullamcorper bibendum iaculis, augue libero ornare urna, sit amet
              scelerisque arcu eros nec mi. Nam at metus a nisl varius tempus.
              Sed nec lacinia sem, sed fermentum nibh. Pellentesque et posuere
              sapien. Duis ornare, mauris mattis pulvinar venenatis, metus enim
              facilisis ante, id feugiat leo nisi ut diam. Sed vel diam eu nunc
              placerat dapibus nec ut mi.
            </p>
          </CSSTransition>
        </div>
      </div>
      <CSSTransition
        in={firstTag}
        classNames="tag1"
        timeout={{ enter: 2000, exit: 2000 }}
        unmountOnExit
      >
        <TagCard title={"content"} tag={"/content"} />
      </CSSTransition>
      <CSSTransition in={firstTag} classNames="tag2" timeout={3000}>
        <TagCard title={"portfolio"} tag={"/portfolio"} />
      </CSSTransition>
      <CSSTransition in={firstTag} classNames="tag3" timeout={4000}>
        <TagCard title={"investment"} tag={"#"} />
      </CSSTransition>
      <CSSTransition in={firstTag} classNames="tag4" timeout={5000}>
        <TagCard title={"contact us"} tag={"/contactus"} />
      </CSSTransition>
    </div>
  );
}

export default Main;
