// import styles from "./Investment.module.css";
import { useEffect, useState } from "react";
import styles from "./Investment.module.css";
import axios from "axios";

export default function InvestMent({ user }) {
  //유저 계좌
  const [userAccount, setUserAccount] = useState([]);
  //유저주식리스트
  const [userStock, setUserStock] = useState([]);
  //주식개요정보
  const [StockInfo, setStockInfo] = useState([]);
  //검색때 쓰는 상태관리
  const [liveData, setLiveData] = useState([]);
  //검색해서 선택된주식정보
  const [selectStock, setSelectStock] = useState([]);
  //검색때 사용하는 온채인지
  const [inputValue, setInputValue] = useState("");
  //검색된 주식리스트
  const [stockList, setStockList] = useState([]);
  //실시간 가격리스트
  const [stockPriceList, setStockPriceList] = useState([]);
  //구매하거나 판매할 주식의 수량
  const [countStock, setCountStock] = useState(0);
  //유저의 총 매입가
  const [allbuy, setAllBuy] = useState([]);
  //주식 상세 정보
  const [jusikDetail, setJusikDetail] = useState([]);

  //유저의 총 매입가 가져오기
  const getUserAllBuy = () => {
    if (user.id) {
      axios
        .get(
          `https://port-0-alltu-toza-6g2llfxqu2r0.sel3.cloudtype.app/useraccount?id=${user.id}`
        )
        .then((res) => res.data)
        .then((data) => {
          setAllBuy(data);
          console.log(data);
        });
    }
  };
  //<유저계좌생성or반환>투자 입장했을때 user의 계좌가 없으면 생성하고 이미있으면 계좌 반환
  useEffect(() => {
    if (user.length !== 0) {
      axios
        .post(
          `https://port-0-alltu-toza-6g2llfxqu2r0.sel3.cloudtype.app/user`,
          {
            id: user.id,
            name: user.name,
          }
        )
        .then((response) => {
          setUserAccount(response.data);
          setUserStock(response.data.stocks);
        });
    }
    getUserAllBuy();
  }, [user]);
  //주식디테일받기
  const getJusikDetail = (ticker, company) => {
    axios
      .get(
        `https://port-0-alltu-toza-6g2llfxqu2r0.sel3.cloudtype.app/stockinfo?id=${user.id}&ticker=${ticker}`
      )
      .then((res) => res.data)
      .then((data) => {
        setJusikDetail(data);
        console.log(data);
      });
  };
  useEffect(() => {
    //생데이터받아오기
    fetch(
      `https://port-0-alltu-search-6g2llfxqu2r0.sel3.cloudtype.app/search/nas`
    )
      .then((res) => res.json())
      .then((data) => {
        setLiveData(data);
      });
    //주식리스트받기
    fetch(
      `https://port-0-alltu-search-6g2llfxqu2r0.sel3.cloudtype.app/search/nas`
    )
      .then((res) => res.json())
      .then((data) => {
        setStockList(data);
      });
    //초기 가격정보 받기
    stockPrice();
    //다음 가격정보 받기
    setInterval(() => {
      stockPrice();
    }, 30000);
  }, []);
  //단일주식정보가져오기
  const getStockInfo = async (ticker) => {
    console.log("스탁인포시작");
    axios
      .post(
        "https://vzjj45kvapsj55iz7apot27wiq0ddvnm.lambda-url.ap-northeast-2.on.aws/",
        {
          ticker: ticker,
        }
      )
      .then((response) => {
        setStockInfo(response.data.body);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  //현재 가격정보가져오기
  const stockPrice = () => {
    axios
      .get(
        "https://port-0-alltu-toza-6g2llfxqu2r0.sel3.cloudtype.app/stocklist"
      )
      .then((res) => res.data)
      .then((data) => {
        setStockPriceList(data);
        console.log("데이터받아옴");
      });
  };
  //가격 자동화

  //주식 클릭
  const clickJu = (ticker, company) => {
    setSelectStock({ ticker: ticker, company: company });
    getStockInfo(ticker);
  };
  //내 주식 클릭
  const clickMyjusik = (ticker, company) => {
    setSelectStock({ ticker: ticker, company: company });
    getStockInfo(ticker);
    getJusikDetail(ticker, company);
  };
  //검색함수
  const inputChange = (e) => {
    fetch(
      `https://port-0-alltu-search-6g2llfxqu2r0.sel3.cloudtype.app/search/nas?company=${e.target.value}`
    )
      .then((res) => res.json())
      .then((data) => {
        setStockList(data);
      });
  };
  //주식구매
  const buyjusik = (ticker, count, company) => {
    axios
      .post(
        `https://port-0-alltu-toza-6g2llfxqu2r0.sel3.cloudtype.app/buystock?id=${user.id}&ticker=${ticker}&count=${count}&company=${company}`
      )
      .then((res) => res.data)
      .then((data) => {
        alert(data);
        window.location.reload();
      });
    setCountStock(0);
  };
  //주식판매
  const selljusik = (ticker, count, company) => {
    axios
      .post(
        `https://port-0-alltu-toza-6g2llfxqu2r0.sel3.cloudtype.app/sellstock?id=${user.id}&ticker=${ticker}&count=${count}&company=${company}`
      )
      .then((res) => res.data)
      .then((data) => {
        alert(data);
        window.location.reload();
      });
    setCountStock(0);
  };

  return (
    <div className={styles.container}>
      <div className={styles.searchArea}>
        <div className={styles.searchHead}>
          <h1>검색부분</h1>
          <input onChange={inputChange} />
          <div className={styles.serachDropDownBox}>
            {stockList.length == 0 ? (
              <p>해당 검색어가 없습니다.</p>
            ) : (
              stockList.map((item, index) => (
                <div
                  key={index}
                  onClick={() => {
                    clickJu(item.ticker, item.company);
                  }}
                >
                  회사: {item.company} 종목코드:{item.ticker} 가격:
                  {stockPriceList[String(item.ticker)]}원
                </div>
              ))
            )}
          </div>
        </div>
        {/* <div>
          <h1>차트넣으면 좋을듯</h1>
        </div>
        <div className={styles.stockInfo}>
          <h1>주식정보부분</h1>
          {StockInfo === "" ? (
            <h5>null</h5>
          ) : (
            <>
              <h5>
                {StockInfo.company} : {StockInfo.ticker}
              </h5>
              <h5>개요 : {StockInfo.summaryInfo}</h5>
              <h5>업종 : {StockInfo.category}</h5>
              <h5>시총 : {StockInfo.marketSum}</h5>
            </>
          )}
        </div> */}
      </div>
      {/* <div className={styles.invenArea}>
        <div className={styles.inven}>
          <div className={styles.inventory}>
            <h1>유저가 가지고있는 주식</h1>
            <div style={{ overflow: "auto" }}>
              {Object.keys(userStock).length == 0 ? (
                <>리스트없음</>
              ) : (
                Object.keys(userStock).map((item, index) => (
                  <div
                    key={index}
                    onClick={() => {
                      clickMyjusik(
                        userStock[item].ticker,
                        userStock[item].company
                      );
                    }}
                  >
                    <p>
                      종목코드:{userStock[item].ticker} , 종목명:
                      {userStock[item].company} 매입가 :
                      {userStock[item].buyprice}원
                    </p>
                  </div>
                ))
              )}
            </div>
          </div>
          <div className={styles.invenlist}>
            <h1>유저의 주식현황</h1>
            {Object.keys(allbuy).length == 0 ? (
              <>리스트없음</>
            ) : (
              <>
                <p>현재 잔액 : {userAccount.money}</p>
                <p>총 매입가:{allbuy.buyStockprice}</p>
                <p>총 현재가:{allbuy.nowStockprice}</p>
              </>
            )}
            <h3>주식 상세 페이지</h3>
            {Object.keys(jusikDetail).length == 0 ? (
              <>상세페이지선택안됨</>
            ) : (
              <>
                <h5>
                  회사명{selectStock.company}:종목코드{jusikDetail.ticker}
                </h5>
                <h5>수량{jusikDetail.count}</h5>
                <h5>
                  매입가:{jusikDetail.buyMoney} 현재가:{jusikDetail.nowMoney}
                </h5>
                <h5>수익{jusikDetail.benefit}</h5>
                <h5>수익률{jusikDetail.stockReturn}</h5>
              </>
            )}
          </div>
        </div>
        <div className={styles.buySell}>
          <h1>구매판매구역</h1>
          <p>
            종목명: {selectStock.company} 종목코드: {selectStock.ticker}
          </p>
          <input
            value={countStock}
            onChange={(e) => {
              setCountStock(e.target.value);
            }}
          />
          <button
            onClick={() => {
              buyjusik(selectStock.ticker, countStock, selectStock.company);
            }}
          >
            구매
          </button>
          <button
            onClick={() => {
              selljusik(selectStock.ticker, countStock, selectStock.company);
            }}
          >
            판매
          </button>
        </div>
      </div> */}
    </div>
  );
}
