import styles from "../header/Header.module.css";
import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./styles.css";
import { CSSTransition } from "react-transition-group";

export let onContactUsPage = false;

export default function Header({ user }) {
  const [scrollValue, setScrollValue] = useState(0);

  const [content, setContent] = useState(false);
  const [portfolio, setPortfolio] = useState(false);
  const [contactUs, setContactUs] = useState(false);

  const [loginModal, setLoginModal] = useState(false);

  const REST_API_KEY = "be53e2150bbff9d4927bbf17a7104d90";
  const REDIRECT_URI = "http://192.168.31.157:3000/kakaologin";
  const navi = useNavigate();

  useEffect(() => {
    const handleScroll = () => {
      const position = window.pageYOffset;
      setScrollValue(position);
    };

    window.addEventListener("scroll", handleScroll, { passive: true });

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  onContactUsPage = contactUs;

  const logout = () => {
    localStorage.removeItem("token");
    navi("/");
    window.location.reload();
  };
  return (
    <>
      {scrollValue > 100 ? (
        <div className={styles.scrollNavContainer}>
          <div className={styles.logoContainer}>
            <Link exact to="/">
              <div className={styles.logo}></div>
            </Link>
            <div className={styles.logoCircle}></div>
          </div>
          <ul className={styles.nav}>
            <Link to="/content">
              {content ? (
                <li className={styles.navlist} style={{ color: "yellow" }}>
                  Content
                </li>
              ) : (
                <li
                  className={styles.navlist}
                  onClick={() => {
                    setContent(true);
                    setPortfolio(false);
                    setContactUs(false);
                  }}
                >
                  Content
                </li>
              )}
            </Link>
            <Link to="/portfolio">
              {portfolio ? (
                <li className={styles.navlist} style={{ color: "yellow" }}>
                  portfolio
                </li>
              ) : (
                <li
                  className={styles.navlist}
                  onClick={() => {
                    setPortfolio(true);
                    setContent(false);
                    setContactUs(false);
                  }}
                >
                  Portfolio
                </li>
              )}
            </Link>
            <li className={styles.navlist}>Game</li>
            <Link to="/contactus">
              {contactUs ? (
                <li className={styles.navlist} style={{ color: "yellow" }}>
                  Contact Us
                </li>
              ) : (
                <li
                  className={styles.navlist}
                  onClick={() => {
                    setContactUs(true);
                    setPortfolio(false);
                    setContent(false);
                  }}
                >
                  Contact Us
                </li>
              )}
            </Link>
            {!user.name ? (
              <li className={styles.navlist}>Log In</li>
            ) : (
              <li className={styles.navlist}>{user.name} 님</li>
            )}
          </ul>
        </div>
      ) : (
        <div className={styles.navContainer}>
          <div className={styles.logoContainer}>
            <Link exact to="/">
              <div className={styles.logo}></div>
            </Link>
            <div className={styles.logoCircle}></div>
          </div>
          <ul className={styles.nav}>
            <Link to="/content">
              {content ? (
                <li className={styles.navlist} style={{ color: "yellow" }}>
                  Content
                </li>
              ) : (
                <li
                  className={styles.navlist}
                  onClick={() => {
                    setContent(true);
                    setPortfolio(false);
                    setContactUs(false);
                  }}
                >
                  Content
                </li>
              )}
            </Link>
            <Link to="/portfolio">
              {portfolio ? (
                <li className={styles.navlist} style={{ color: "yellow" }}>
                  Portfolio
                </li>
              ) : (
                <li
                  className={styles.navlist}
                  onClick={() => {
                    setContent(false);
                    setPortfolio(true);
                    setContactUs(false);
                  }}
                >
                  Portfolio
                </li>
              )}
            </Link>
            <li className={styles.navlist}>Game</li>
            <Link to="/contactus">
              {contactUs ? (
                <li className={styles.navlist} style={{ color: "yellow" }}>
                  Contact Us
                </li>
              ) : (
                <li
                  className={styles.navlist}
                  onClick={() => {
                    setContactUs(true);
                    setPortfolio(false);
                    setContent(false);
                  }}
                >
                  Contact Us
                </li>
              )}
            </Link>

            <a
              href="#"
              onClick={() => {
                setLoginModal((prev) => !prev);
              }}
            >
              {!user.name ? (
                <li className={styles.navlist}>Log In</li>
              ) : (
                <li className={styles.navlist}>{user.name} 님</li>
              )}
            </a>
            {loginModal === true ? (
              <CSSTransition
                in={loginModal}
                classNames="modalAnim"
                timeout={{ enter: 2000, exit: 200 }}
                unmountOnExit
              >
                <div className={styles.darkness}>
                  {!user.name ? (
                    <div className={styles.modalContainer}>
                      <div
                        className={styles.closeBtn}
                        onClick={() => {
                          setLoginModal(false);
                        }}
                      ></div>
                      <h2>올바른 투자 습관</h2>
                      <div className={styles.logo}></div>
                      <a
                        href={`https://kauth.kakao.com/oauth/authorize?response_type=code&client_id=${REST_API_KEY}&redirect_uri=${REDIRECT_URI}`}
                      >
                        <div className={styles.kakaoContainer}>
                          <div className={styles.kakaoLogo}></div>
                          <span>카카오로 로그인</span>
                        </div>
                      </a>
                      <h2>Please Login</h2>
                    </div>
                  ) : (
                    <button
                      onClick={() => {
                        logout();
                      }}
                    >
                      <div className={styles.kakao}>로그아웃</div>
                    </button>
                  )}
                </div>
              </CSSTransition>
            ) : null}
          </ul>
        </div>
      )}
    </>
  );
}
