import axios from "axios";
import { useEffect, useState } from "react";
import styles from "./Portfolio.module.css";

export default function Portfolio({ user }) {
  const [userPort, setUserPort] = useState([]);

  const [visibleModal, setVisibleModal] = useState(false);

  useEffect(() => {
    axios
      .post(
        `https://port-0-alltu-portpoilo-6g2llfxqu2r0.sel3.cloudtype.app/portpoilo?id=${user.id}`
      )
      .then((response) => setUserPort(response.data));
  }, []);

  console.log(userPort.portpoiloList);
  return (
    <>
      <div
        style={{
          position: "absolute",
          top: 0,
          right: 0,
          width: "100%",
          height: "100%",
          background: "rgba(0, 0, 0, 0.6)",
          zIndex: 95,
        }}
      ></div>
      <div className={styles.container}>
        {userPort.portpoiloList ? (
          <h1>리스트 없음</h1>
        ) : (
          <div className={styles.card}>
            <div className={styles.imgBx}>
              <img src="https://ifh.cc/g/twHjyo.png" alt="asdf"></img>
            </div>
            <div className={styles.content}>
              <h2>Apple</h2>
              <p>Apple의 내용입니다.</p>
              <a href="https://www.apple.com/">Apple로 가기</a>
            </div>
          </div>
        )}
        <div className={styles.card}>
          <div className={styles.imgBx}>
            <img src="https://ifh.cc/g/Snps7L.png" alt="asdf" />
          </div>
          <div className={styles.content}>
            <h2>Google</h2>
            <p>Google의 내용입니다</p>
          </div>
        </div>
        <div className={styles.card}>
          <div className={styles.imgBx}>
            <img src="https://ifh.cc/g/L3MYOo.png" alt="asdf"></img>
          </div>
          <div className={styles.content}>
            <h2>MicroSoft</h2>
            <p>MicroSoft의 내용입니다</p>
          </div>
        </div>
        <button
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            backgroundColor: "white",
            width: 100,
            height: 100,
            borderRadius: "50%",
            fontSize: 50,
          }}
          onClick={() => {
            setVisibleModal(true);
          }}
        >
          +
        </button>
      </div>
    </>
  );
}
