import React, { useEffect, useState } from "react";
import Header, { scroll } from "../../component/header/Header";
import styles from "../main/Main.module.css";
import ContactUs from "../../component/contents/contactus/ContactUs";
import TagCard from "../../component/maintag/TagCard";
import { CSSTransition } from "react-transition-group";
import "./styles.css";

function Main() {
  const [firstTag, setFirstTag] = useState(false);
  const [scrollValue, setScrollValue] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const position = window.pageYOffset;
      setScrollValue(position);
    };

    window.addEventListener("scroll", handleScroll, { passive: true });

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);
  // useEffect(() => {
  //   setFirstTag(true);
  // }, []);

  return (
    <div className={styles.container}>
      <Header />
      <ContactUs />
      <CSSTransition
        in={firstTag}
        classNames="tag1"
        timeout={{ enter: 2000, exit: 2000 }}
        unmountOnExit
      >
        <TagCard title={"content"} tag={"#"} />
      </CSSTransition>
      <button
        onClick={() => {
          setFirstTag((prev) => !prev);
        }}
      >
        asfdasdf
      </button>
      <CSSTransition in={firstTag} classNames="tag2" timeout={3000}>
        <TagCard title={"phortpolio"} tag={"#"} />
      </CSSTransition>
      <CSSTransition in={firstTag} classNames="tag3" timeout={4000}>
        <TagCard title={"investment"} tag={"#"} />
      </CSSTransition>
      <CSSTransition in={firstTag} classNames="tag4" timeout={5000}>
        <TagCard title={"contact us"} tag={"#"} />
      </CSSTransition>
      <footer className={styles.footer}></footer>
    </div>
  );
}

export default Main;
