import StockWidget from "../../../widget/stockgraph/StockWidget";
import AutoComplete from "../../test/test";
import styles from "./Content.module.css";

// prettier-ignore
const data = `{
  "symbols":[["Apple", "AAPL|1D"],
  ["Google", "GOOGL|1D"]
  ],
  "chartOnly": false,
  "width": "700",
  "height": "450",
  "locale": "en",
  "colorTheme": "dark",
  "autosize": false,
  "showVolume": false,
  "showMA": false,
  "hideDateRanges": false,
  "hideMarketStatus": false,
  "hideSymbolLogo": false,
  "scalePosition": "right",
  "scaleMode": "Normal",
  "fontFamily": "-apple-system, BlinkMacSystemFont, Trebuchet MS, Roboto, Ubuntu, sans-serif",
  "fontSize": "10",
  "noTimeScale": false,
  "valuesTracking": "1",
  "changeMode": "price-and-percent",
  "chartType": "line",
  "maLineColor": "#2962FF",
  "maLineWidth": 1,
  "maLength": 9
}`;

export default function Content(props) {
  const [qeustion, setQeustion] = useState("");
  const [stockData, setStockData] = useState(data);

  const handleSubmit = (e) => {
    e.preventDefault();
    // 서버로 보내는 로직 하나
    setQeustion("");
  };

  const L = () => {
    let JsonData = JSON.parse(data);
    JsonData.symbols.push(["Microsoft", "MSFT|1D"]);
    let LastData = JSON.stringify(JsonData);
    setStockData(LastData);
  };

  L();
  // useEffect(() => {
  //   pushStock();
  // }, []);

  return (
    <div className={styles.main}>
      <div className={styles.stockArea}>
        {/* <div className={styles.stockGraph}></div> */}
        <StockWidget data={stockData} />
        <div className={styles.stockEx}>
          <h5>향후 전망?</h5>
          <h5 style={{ color: "white" }}>{stockData.symbols}</h5>
        </div>
      </div>
      <div className={styles.searchArea}>
        {/* <form onSubmit={handleSubmit}>
          <input
            className={styles.inputBox}
            type="text"
            value={qeustion}
            onChange={(e) => {
              setQeustion(e.target.value);
              console.log(qeustion);
            }}
          />
          <button type="Submit">Submit</button>
        </form> */}
        <AutoComplete />
      </div>
    </div>
  );
}
