import { useEffect, useState } from "react";
import { CSSTransition } from "react-transition-group";
import styles from "./MainPage.module.css";
import "./styles.css";

export default function MainPage() {
  const [anim, setAnim] = useState(false);

  useEffect(() => {
    setAnim(true);
  }, []);

  return (
    <div className={styles.main}>
      <CSSTransition in={anim} classNames="mainImg" timeout={2000}>
        <div className={styles.mainImg}></div>
      </CSSTransition>
      <div className={styles.mainEx}>
        <CSSTransition in={anim} classNames="mainTitle" timeout={2500}>
          <h1>
            당신의 <br />
            올바른 투자 습관을 위해
          </h1>
        </CSSTransition>
        <CSSTransition in={anim} classNames="mainEx" timeout={3000}>
          <span>
            Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vivamus
            eget metus in elit rutrum lobortis. Sed volutpat sagittis sem, eu
            mattis quam luctus ut. Aliquam posuere vulputate leo eget finibus.
            Quisque gravida elit et purus cursus maximus. Duis efficitur, nisl
            ullamcorper bibendum iaculis, augue libero ornare urna, sit amet
            scelerisque arcu eros nec mi. Nam at metus a nisl varius tempus. Sed
            nec lacinia sem, sed fermentum nibh. Pellentesque et posuere sapien.
            Duis ornare, mauris mattis pulvinar venenatis, metus enim facilisis
            ante, id feugiat leo nisi ut diam. Sed vel diam eu nunc placerat
            dapibus nec ut mi.
          </span>
        </CSSTransition>
      </div>
    </div>
  );
}
