export default function Portfolio({ user }) {
  const [userPort, setUserPort] = useState([]);

  const [visibleModal, setVisibleModal] = useState(false);

  // // 검색창 값
  // const [inputValue, setInputValue] = useState("");

  // // 연관검색어
  // const [dropDownList, setDropDownList] = useState([]);
  // const [dropDownData, setDropDownData] = useState([]);

  const [inputValue, setInputValue] = useState("");
  const [isHaveInputValue, setIsHaveInputValue] = useState(false);
  const [dropDownList, setDropDownList] = useState(wholeTextArray);
  const [dropDownItemIndex, setDropDownItemIndex] = useState(-1);

  const showDropDownList = () => {
    if (inputValue === "") {
      setIsHaveInputValue(false);
      setDropDownList([]);
    } else {
      const choosenTextList = wholeTextArray.filter((textItem) =>
        textItem.includes(inputValue)
      );
      setDropDownList(choosenTextList);
    }
  };

  const changeInputValue = (event) => {
    axios
      .get(
        "https://port-0-alltu-search-6g2llfxqu2r0.sel3.cloudtype.app/search/nas"
      )
      .then((response) => response.data);

    setInputValue(event.target.value);
    setIsHaveInputValue(true);
  };

  const clickDropDownItem = (clickedItem) => {
    setInputValue(clickedItem);
    setIsHaveInputValue(false);
  };

  const handleDropDownKey = (event) => {
    //input에 값이 있을때만 작동
    if (isHaveInputValue) {
      if (
        event.key === "ArrowDown" &&
        dropDownList.length - 1 > dropDownItemIndex
      ) {
        setDropDownItemIndex(dropDownItemIndex + 1);
      }

      if (event.key === "ArrowUp" && dropDownItemIndex >= 0)
        setDropDownItemIndex(dropDownItemIndex - 1);
      if (event.key === "Enter" && dropDownItemIndex >= 0) {
        clickDropDownItem(dropDownList[dropDownItemIndex]);
        setDropDownItemIndex(-1);
      }
    }
  };

  useEffect(showDropDownList, [inputValue]);

  //
  useEffect(() => {
    console.log("포토폴리오 유저", typeof user);
    if (user.length !== 0) {
      axios
        .post(
          `https://port-0-alltu-portpoilo-6g2llfxqu2r0.sel3.cloudtype.app/portpoilo?id=${user.id}`
        )
        .then((response) => {
          setUserPort(response.data);
        });
    }
  }, [user]);

  useEffect(() => {}, []);

  const search = (e) => {
    console.log(e.target.value);
    setInputValue(e.target.value);
    axios
      .get(
        `https://port-0-alltu-search-6g2llfxqu2r0.sel3.cloudtype.app/search?company=${inputValue}`
      )
      .then((response) => setDropDownList(response.data));
  };
  // console.log(dropDownList);

  const sendInputValue = (e) => {
    e.preventDefault();
    console.log("함수진입", e.target.value);
    setInputValue("");
  };
  const getPortFolioList = (portpoiloList) => {
    if (Array.isArray(portpoiloList)) {
      return portpoiloList.map((item, index) => (
        <div key={index} className={styles.card}>
          <div className={styles.imgBx}>
            <img src={item.img} alt="asdf"></img>
          </div>
          <div className={styles.content}>
            <h2>
              fdf {item.company} : {item.ticker}
            </h2>
            <p>fdsfsd</p>
            <a href="https://www.apple.com/">Apple로 가기</a>
          </div>
        </div>
      ));
    } else {
      <h1>로그인 하세요</h1>;
    }
  };

  return (
    <>
      {visibleModal ? (
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            position: "absolute",
            top: 0,
            right: 0,
            width: "100%",
            height: "100%",
            background: "rgba(0, 0, 0, 0.6)",
            zIndex: 95,
          }}
        >
          <div style={{ width: 300, height: 300, backgroundColor: "white" }}>
            <button
              onClick={() => {
                setVisibleModal(false);
              }}
            >
              X
            </button>
            {/* <form
              onSubmit={(e) => {
                e.preventDefault();
                console.log(inputValue);
                setInputValue("");
              }}
            >
              <input type="text" value={inputValue} onChange={search}></input>
            </form> */}
            <WholeBox>
              <InputBox
                className={styles.inputBox}
                isHaveInputValue={isHaveInputValue}
              >
                <form onSubmit={sendInputValue}>
                  <Input
                    type="text"
                    value={inputValue}
                    onChange={changeInputValue}
                    onKeyUp={handleDropDownKey}
                  />
                </form>
                <SearchButton onClick={() => {}}>&#43;</SearchButton>
              </InputBox>
              {isHaveInputValue && (
                <DropDownBox>
                  {dropDownList.length === 0 && (
                    <DropDownItem>해당하는 단어가 없습니다</DropDownItem>
                  )}
                  {dropDownList.map((dropDownItem, dropDownIndex) => {
                    return (
                      <DropDownItem
                        key={dropDownIndex}
                        onClick={() => clickDropDownItem(dropDownItem)}
                        onMouseOver={() => setDropDownItemIndex(dropDownIndex)}
                        className={
                          dropDownItemIndex === dropDownIndex ? "selected" : ""
                        }
                      >
                        {dropDownItem}
                      </DropDownItem>
                    );
                  })}
                </DropDownBox>
              )}
            </WholeBox>
            모달입니다
          </div>
        </div>
      ) : null}
      <div className={styles.container}>
        {userPort !== "" ? (
          getPortFolioList(userPort.portpoiloList)
        ) : (
          // <h1>리스트 있음</h1>
          <h1>리스트 없음</h1>
        )}
        <button
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            backgroundColor: "white",
            width: 100,
            height: 100,
            borderRadius: "50%",
            fontSize: 50,
          }}
          onClick={() => {
            setVisibleModal(true);
          }}
        >
          +
        </button>
      </div>
    </>
  );
}

