import styles from "../header/Header.module.css";
import $ from "jquery";

export default function Header() {
  const scroll = $(window).scrollTop();
  console.log(scroll);

  if (scroll > 200) {
    $(".navContainer").css({
      background: "#e3dcd6",
      transition: "all, linear, 1s",
    });
    // $(".bk").hide();
  } else {
    $(".navContainer").css("background", "rgb(44, 44, 44)");
    // $(".bk").show();
  }

  return (
    <>
      {scroll > 200 ? (
        <div className={styles.scrollNavContainer}>
          <div></div>
          <ul
            style={{
              display: "flex",
              justifyContent: "space-around",
              alignItems: "center",
              listStyle: "none",
            }}
          >
            <li className={styles.navlist}>Content</li>
            <li className={styles.navlist}>PhortPolio</li>
            <li className={styles.navlist}>Contact Us</li>
          </ul>
        </div>
      ) : (
        <div className={styles.navContainer}>
          <div></div>
          <ul
            style={{
              display: "flex",
              justifyContent: "space-around",
              alignItems: "center",
              listStyle: "none",
            }}
          >
            <li className={styles.navlist}>Content</li>
            <li className={styles.navlist}>PhortPolio</li>
            <li className={styles.navlist}>Contact Us</li>
          </ul>
        </div>
      )}
    </>
  );
}
