import styles from "../header/Header.module.css";
import $ from "jquery";

export default function Header() {
  $(document).ready(() => {
    $(window).scroll(() => {
      const scroll = $(window).scrollTop();
      console.log(scroll);
      if (scroll > 200) {
        $(".navbar").css({
          background: "#e3dcd6",
          transition: "all, linear, 1s",
        });
        $(".bk").hide();
      } else {
        $(".navbar").css("background", "white");
        $(".bk").show();
      }
    });
  });

  return (
    <div className={styles.container}>
      <div></div>
      <ul
        style={{
          display: "flex",
          justifyContent: "space-around",
          alignItems: "center",
          listStyle: "none",
        }}
      >
        <li className={styles.navlist}>Content</li>
        <li className={styles.navlist}>PhortPolio</li>
        <li className={styles.navlist}>Contact Us</li>
      </ul>
    </div>
  );
}
