// TradingViewWidget.jsx
import React, { useEffect, useRef, memo, useState } from "react";

// prettier-ignore
const data = `{
  "symbols":[["Apple", "AAPL|1D"],
  ["Google", "GOOGL|1D"]
  ],
  "chartOnly": false,
  "width": "700",
  "height": "450",
  "locale": "en",
  "colorTheme": "dark",
  "autosize": false,
  "showVolume": false,
  "showMA": false,
  "hideDateRanges": false,
  "hideMarketStatus": false,
  "hideSymbolLogo": false,
  "scalePosition": "right",
  "scaleMode": "Normal",
  "fontFamily": "-apple-system, BlinkMacSystemFont, Trebuchet MS, Roboto, Ubuntu, sans-serif",
  "fontSize": "10",
  "noTimeScale": false,
  "valuesTracking": "1",
  "changeMode": "price-and-percent",
  "chartType": "line",
  "maLineColor": "#2962FF",
  "maLineWidth": 1,
  "maLength": 9
}`;
let symbolslist = JSON.parse(data).symbols;
symbolslist.push(["Microsoft", "MSFT|1D"]);
console.log(symbolslist);
function TradingViewWidget() {
  const contariner = useRef();
  const [stockData, setStockData] = useState(data);

  useEffect(() => {
    const script = document.createElement("script");
    script.src =
      "https://s3.tradingview.com/external-embedding/embed-widget-symbol-overview.js";
    script.type = "text/javascript";
    script.async = true;
    script.innerHTML = stockData;
    contariner.current.appendChild(script);
  }, []);

  return (
    <div className="tradingview-widget-container" ref={contariner}>
      <div className="tradingview-widget-container__widget"></div>
      <div className="tradingview-widget-copyright">
        <a
          href="https://www.tradingview.com/symbols/AAPL/"
          rel="noopener"
          target="_blank"
        >
          <span className="blue-text">Apple</span>
        </a>{" "}
        by TradingView
      </div>
    </div>
  );
}

export default memo(TradingViewWidget);
