// TradingViewWidget.jsx
import React, { useEffect, useRef, memo, useState } from "react";

function TradingViewWidget(props) {
  const contariner = useRef();

  useEffect(() => {
    if (document.querySelector("script") !== null) {
      const sc = document.querySelector("script");
      console.log(typeof sc);
      sc.remove();
    }
    const script = document.createElement("script");
    script.src =
      "https://s3.tradingview.com/external-embedding/embed-widget-symbol-overview.js";
    script.type = "text/javascript";
    script.async = true;
    script.innerHTML = props.data;
    contariner.current.appendChild(script);
  }, [props.data]);

  return (
    <div className="tradingview-widget-container" ref={contariner}>
      <div className="tradingview-widget-container__widget"></div>
      <div className="tradingview-widget-copyright">
        <a
          href="https://www.tradingview.com/symbols/AAPL/"
          rel="noopener"
          target="_blank"
        >
          <span className="blue-text">Apple</span>
        </a>{" "}
        by TradingView
      </div>
    </div>
  );
}

export default memo(TradingViewWidget);
