import { useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";

export default function Loading() {
  const location = useLocation();
  const navi = useNavigate();

  useEffect(() => {
    const KAKAO_CODE = window.location.search.split("=")[1];
    const IP = "http://192.168.31.151:8080";

    if (!localStorage.getItem("token") && KAKAO_CODE != null) {
      console.log("asf");
      fetch(`${IP}/join?code=${KAKAO_CODE}`, {
        method: "GET",
      })
        .then((res) => {
          console.log("rrrr: ", res);
          return res.headers.get("Authorization");
        })
        .then((token) => {
          localStorage.setItem("token", token);
          window.location.reload();
        });
    }
  }, []);
  return (
    <div style={{ width: 200, height: 200, backgroundColor: "white" }}>
      <h1>Loading...</h1>
    </div>
  );
}
