import React, { useEffect, useState } from "react";
import Header from "../../component/header/Header";
import styles from "../main/Main.module.css";
import ContactUs from "../../component/contents/contactus/ContactUs";
import TagCard from "../../component/maintag/TagCard";
import { CSSTransition } from "react-transition-group";
import "./styles.css";

function Main() {
  const [firstTag, setFirstTag] = useState(false);

  // const location = useLocation();
  // const navi = useNavigate();
  // const KAKAO_CODE = location.search.split("=")[1];
  //   console.log(KAKAO_CODE);
  // console.log("param", param);

  useEffect(() => {
    const KAKAO_CODE = window.location.search.split("=")[1];
    const IP = "http://192.168.31.151:8080";

    if (!localStorage.getItem("token") && KAKAO_CODE != null) {
      console.log("asf");
      fetch(`${IP}/join?code=${KAKAO_CODE}`, {
        method: "GET",
      })
        .then((res) => {
          console.log("rrrr: ", res);
          return res.headers.get("Authorization");
        })
        .then((token) => localStorage.setItem("token", token))
        .then(() => {
          window.location.href("/");
        });
    }

    setFirstTag(true);
  }, []);

  return (
    <div className={styles.container}>
      <Header />
      <ContactUs />
      <CSSTransition
        in={firstTag}
        classNames="tag1"
        timeout={{ enter: 2000, exit: 2000 }}
        unmountOnExit
      >
        <TagCard title={"content"} tag={"#"} />
      </CSSTransition>
      <CSSTransition in={firstTag} classNames="tag2" timeout={3000}>
        <TagCard title={"phortpolio"} tag={"#"} />
      </CSSTransition>
      <CSSTransition in={firstTag} classNames="tag3" timeout={4000}>
        <TagCard title={"investment"} tag={"#"} />
      </CSSTransition>
      <CSSTransition in={firstTag} classNames="tag4" timeout={5000}>
        <TagCard title={"contact us"} tag={"#"} />
      </CSSTransition>
      <footer className={styles.footer}></footer>
    </div>
  );
}

export default Main;
