import axios from "axios";
import { useEffect, useState } from "react";
import styles from "./Portfolio.module.css";

export default function Portfolio({ user }) {
  const [userPort, setUserPort] = useState([]);

  const [visibleModal, setVisibleModal] = useState(false);

  useEffect(() => {
    console.log("포토폴리오 받아옴");
    if (user != "") {
      axios
        .post(
          `https://port-0-alltu-portpoilo-6g2llfxqu2r0.sel3.cloudtype.app/portpoilo?id=${user.id}`
        )
        .then((response) => {
          setUserPort(response.data);
        });
    }
  }, [user]);

  // console.log(userPort);

  const a = [1, 2, 3, 4];

  const test = (portpoiloList) => {
    portpoiloList = Array(portpoiloList);
    if (Array.isArray(portpoiloList)) {
      console.log("이프문 뚤림");
      console.log(portpoiloList);
      return portpoiloList.map((item) => {
        console.log(item.ticker);
        return (
          <div key={item.ticker} className={styles.card}>
            <div className={styles.imgBx}>
              <img src={item.img} alt="asdf"></img>
            </div>
            <div className={styles.content}>
              <h2>
                fdf {item.company} : {item.ticker}
              </h2>
              <p>fdsfsd</p>
              <a href="https://www.apple.com/">Apple로 가기</a>
            </div>
          </div>
        );
      });
    }
  };

  return (
    <>
      {visibleModal ? (
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            position: "absolute",
            top: 0,
            right: 0,
            width: "100%",
            height: "100%",
            background: "rgba(0, 0, 0, 0.6)",
            zIndex: 95,
          }}
        >
          <div style={{ width: 300, height: 300, backgroundColor: "white" }}>
            모달입니다
          </div>
          <button
            onClick={() => {
              setVisibleModal(false);
            }}
          >
            X
          </button>
        </div>
      ) : null}
      <div className={styles.container}>
        {userPort != "" ? test(userPort.portpoiloList) : <h1>리스트 없음</h1>}
        <div className={styles.card}>
          <div className={styles.imgBx}>
            <img src="https://ifh.cc/g/Snps7L.png" alt="asdf" />
          </div>
          <div className={styles.content}>
            <h2>Google</h2>
            <p>Google의 내용입니다</p>
          </div>
        </div>
        <div className={styles.card}>
          <div className={styles.imgBx}>
            <img src="https://ifh.cc/g/L3MYOo.png" alt="asdf"></img>
          </div>
          <div className={styles.content}>
            <h2>MicroSoft</h2>
            <p>MicroSoft의 내용입니다</p>
          </div>
        </div>
        <button
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            backgroundColor: "white",
            width: 100,
            height: 100,
            borderRadius: "50%",
            fontSize: 50,
          }}
          onClick={() => {
            setVisibleModal(true);
          }}
        >
          +
        </button>
      </div>
    </>
  );
}
