import React, { useEffect, useState } from "react";
import styles from "../main/Main.module.css";
import ContactUs from "../../component/contents/contactus/ContactUs";
import TagCard from "../../component/maintag/TagCard";
import { CSSTransition } from "react-transition-group";
import "./styles.css";
import StockWidget from "../../widget/stockgraph/StockWidget";

function Main() {
  const [firstTag, setFirstTag] = useState(false);
  const [qeustion, setQeustion] = useState("");

  useEffect(() => {
    setFirstTag(true);
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    // 서버로 보내는 로직 하나
    setQeustion("");
  };

  return (
    <div className={styles.container}>
      <div className={styles.main}>
        <div className={styles.stockArea}>
          {/* <div className={styles.stockGraph}></div> */}
          <StockWidget />
          <div className={styles.stockEx}>
            <h5>향후 전망?</h5>
          </div>
        </div>
        <div className={styles.searchArea}>
          <form onSubmit={handleSubmit}>
            <input
              className={styles.inputBox}
              type="text"
              value={qeustion}
              onChange={(e) => {
                setQeustion(e.target.value);
                console.log(qeustion);
              }}
            />
            <button type="Submit"></button>
          </form>
        </div>
      </div>
      <CSSTransition
        in={firstTag}
        classNames="tag1"
        timeout={{ enter: 2000, exit: 2000 }}
        unmountOnExit
      >
        <TagCard title={"content"} tag={"#"} />
      </CSSTransition>
      <CSSTransition in={firstTag} classNames="tag2" timeout={3000}>
        <TagCard title={"phortpolio"} tag={"#"} />
      </CSSTransition>
      <CSSTransition in={firstTag} classNames="tag3" timeout={4000}>
        <TagCard title={"investment"} tag={"#"} />
      </CSSTransition>
      <CSSTransition in={firstTag} classNames="tag4" timeout={5000}>
        <TagCard title={"contact us"} tag={"#"} />
      </CSSTransition>
    </div>
  );
}

export default Main;
