import axios from "axios";
import { useEffect, useState } from "react";
import styles from "./Portfolio.module.css";
import styled from "styled-components";

export default function Portfolio({ user }) {
  const [userPort, setUserPort] = useState([]);

  const [visibleModal, setVisibleModal] = useState(false);

  useEffect(() => {
    console.log("포토폴리오 유저", typeof user);
    if (user.length !== 0) {
      axios
        .post(
          `https://port-0-alltu-portpoilo-6g2llfxqu2r0.sel3.cloudtype.app/portpoilo?id=${user.id}`
        )
        .then((response) => {
          setUserPort(response.data);
        });
    }
  }, [user]);
  const [stockData, setStockData] = useState(data);

  const [gptValue, setGptValue] = useState("");
  const [gptRes, setGptRes] = useState("");

  // input에 입력한 값 includes를 사용
  const [inputValue, setInputValue] = useState("");

  // input값이 있으면 true 없으면 false
  const [isHaveInputValue, setIsHaveInputValue] = useState(false);

  // dropdwonlist를 DB와 연동해 set을 하면 됨.
  const [dropDownList, setDropDownList] = useState([]);
  const [secondDropDownList, setSecondDropDownList] = useState([]);

  // dropDownList에 있는 요소를 선택하기 위해 마우스를 올렸을 때 효과를 주기 위한 인덱스 번호 설정
  const [dropDownItemIndex, setDropDownItemIndex] = useState(-1);

  // 주식 설명 받아오기
  const [stockInfo, setStockInfo] = useState([]);

  const showDropDownList = () => {
    if (inputValue === "") {
      setIsHaveInputValue(false);
      setDropDownList([]);
    } else {
      const choosenTextList = wholeTextArray.filter((textItem) =>
        textItem.includes(inputValue)
      );
      setDropDownList(choosenTextList);
    }
  };

  useEffect(() => {
    fetch(
      `https://port-0-alltu-search-6g2llfxqu2r0.sel3.cloudtype.app/search/nas`
    )
      .then((res) => res.json())
      .then((data) => {
        setSecondDropDownList(data);
      });
  }, []);

  const changeInputValue = (event) => {
    setInputValue(event.target.value);
    console.log(event.target.value);
    setIsHaveInputValue(true);
    fetch(
      `https://port-0-alltu-search-6g2llfxqu2r0.sel3.cloudtype.app/search?company=${event.target.value}`
    )
      .then((res) => res.json())
      .then((data) => {
        // setSecondDropDownList(data);
        setDropDownList(data);
      });
  };

  const clickDropDownItem = (clickedItem) => {
    setInputValue(clickedItem);
    setIsHaveInputValue(false);
  };

  const handleDropDownKey = (event) => {
    //input에 값이 있을때만 작동
    if (isHaveInputValue) {
      if (
        event.key === "ArrowDown" &&
        dropDownList.length - 1 > dropDownItemIndex
      ) {
        setDropDownItemIndex(dropDownItemIndex + 1);
      }

      if (event.key === "ArrowUp" && dropDownItemIndex >= 0)
        setDropDownItemIndex(dropDownItemIndex - 1);
      if (event.key === "Enter" && dropDownItemIndex >= 0) {
        clickDropDownItem(dropDownList[dropDownItemIndex]);
        setDropDownItemIndex(-1);
      }
    }
  };

  useEffect(showDropDownList, [inputValue]);

  const PushData = (ticker) => {
    let JsonData = JSON.parse(stockData);
    JsonData.symbols.pop(0);
    JsonData.symbols.push([`${inputValue}`, `${ticker}|1D`]);
    let LastData = JSON.stringify(JsonData);
    setStockData(LastData);
  };

  const sendInputValue = (e) => {
    e.preventDefault();
    console.log("함수진입", e.target.value);
    console.log("함수진입", inputValue);
    let ticker = "";
    for (let i of secondDropDownList) {
      console.log("for문 중...");
      if (i.company === inputValue) {
        console.log("티커", i.ticker);
        PushData(i.ticker);
        ticker = i.ticker;
      }
    }
    getStockInfo(ticker);
    setInputValue("");
  };

  const sendInputValueBtn = () => {
    for (let i of secondDropDownList) {
      console.log("for문 중...");
      if (i.company === inputValue) {
        console.log("티커", i.ticker);
        PushData(i.ticker);
      }
    }
    setInputValue("");
  };

  // 구분선

  const getPortFolioList = (portpoiloList) => {
    if (Array.isArray(portpoiloList)) {
      return portpoiloList.map((item) => (
        <div key={item.ticker} className={styles.card}>
          <div className={styles.imgBx}>
            <img src={item.img} alt="asdf"></img>
          </div>
          <div className={styles.content}>
            <h2>
              fdf {item.company} : {item.ticker}
            </h2>
            <p>fdsfsd</p>
            <a href="https://www.apple.com/">Apple로 가기</a>
          </div>
        </div>
      ));
    } else {
      <h1>로그인 하세요</h1>;
    }
  };

  return (
    <>
      {visibleModal ? (
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            position: "absolute",
            top: 0,
            right: 0,
            width: "100%",
            height: "100%",
            background: "rgba(0, 0, 0, 0.6)",
            zIndex: 95,
          }}
        >
          <div style={{ width: 300, height: 300, backgroundColor: "white" }}>
            <WholeBox>
              {/* <OutlineInputBox> */}
              <InputBox
                className={styles.inputBox}
                isHaveInputValue={isHaveInputValue}
              >
                <form onSubmit={sendInputValue}>
                  <Input
                    type="text"
                    value={inputValue}
                    onChange={changeInputValue}
                    onKeyUp={handleDropDownKey}
                  />
                </form>
                <SearchButton
                  onClick={() => {
                    sendInputValueBtn();
                  }}
                >
                  &#43;
                </SearchButton>
              </InputBox>
              {/* </OutlineInputBox> */}
              {isHaveInputValue && (
                <DropDownBox>
                  {dropDownList.length === 0 && (
                    <DropDownItem>해당하는 단어가 없습니다</DropDownItem>
                  )}
                  {dropDownList.map((dropDownItem, dropDownIndex) => {
                    return (
                      <DropDownItem
                        key={dropDownIndex}
                        onClick={() => clickDropDownItem(dropDownItem)}
                        onMouseOver={() => setDropDownItemIndex(dropDownIndex)}
                        className={
                          dropDownItemIndex === dropDownIndex ? "selected" : ""
                        }
                      >
                        {dropDownItem}
                      </DropDownItem>
                    );
                  })}
                </DropDownBox>
              )}
            </WholeBox>
          </div>
          <button
            onClick={() => {
              setVisibleModal(false);
            }}
          >
            X
          </button>
        </div>
      ) : null}
      <div className={styles.container}>
        {userPort !== "" ? (
          getPortFolioList(userPort.portpoiloList)
        ) : (
          // <h1>리스트 있음</h1>
          <h1>리스트 없음</h1>
        )}
        <button
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            backgroundColor: "white",
            width: 100,
            height: 100,
            borderRadius: "50%",
            fontSize: 50,
          }}
          onClick={() => {
            setVisibleModal(true);
          }}
        >
          +
        </button>
      </div>
    </>
  );
}

const WholeBox = styled.div`
  padding: 10px;
`;

const OutlineInputBox = styled.div`
  border: 7px solid black;
  border-radius: 5px;
`;

const InputBox = styled.div`
  display: flex;
  flex-direction: row;
  padding: 16px;
  border: 7px solid black;
  border-radius: 20px;
  box-shadow: 3px 3px 3px 3px rgba(0, 0, 0, 0.3);

  &:focus-within {
    box-shadow: 0 10px 10px rgb(0, 0, 0, 0.3);
  }
`;

const Input = styled.input`
  flex: 1;
  margin: 0;
  background-color: transparent;
  border: none;
  outline: none;
  font-size: 32px;
  width: 15vw;
`;

const SearchButton = styled.div`
  cursor: pointer;
  padding: 0;
  margin: 0;
  color: white;
  font-size: 30px;
`;

const DropDownBox = styled.ul`
  display: block;
  margin: 0 auto;
  padding: 8px 0;
  background-color: white;
  border: 10px solid rgba(0, 0, 0, 1);
  border-top: none;
  border-radius: 0 0 16px 16px;
  box-shadow: 0 10px 10px rgb(0, 0, 0, 0.3);
  list-style-type: none;
  z-index: 3;
`;

const DropDownItem = styled.li`
  padding: 0 16px;

  &.selected {
    background-color: lightgray;
  }
`;
